css:
.header {
    display: flex;
    display: -webkit-box;
    justify-content: justify;
    -webkit-box-pack: justify;
    border-bottom: 1px solid #dddddd;
    padding-bottom: 4px;
}

.invoice-container {
  font-family: Arial, sans-serif;
  color: #333;

  margin: auto;
  box-sizing: border-box;
}


.patient-info,
.invoice-details {
  margin-bottom: 20px;
}

h3 {
  border-bottom: 2px solid #333;
  padding-bottom: 5px;
  margin-bottom: 10px;
}

.invoice-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;
}

.invoice-table th,
.invoice-table td {
  border: 1px solid #ddd;
  padding: 10px;
  text-align: left;
  font-size: 12px;
}

.invoice-table th {
  background-color: #f5f5f5;
}

.total-section {
  text-align: right;
  font-size: 16px;
  font-weight: bold;
}

.note-section {
  margin-top: 20px;
  text-align: center;
  font-style: italic;
}

html:
{% set company = frappe.db.get_value("Company", doc.company, ["*"], as_dict = true) %}
{% set letter_head = frappe.db.get_value("Letter Head", company.default_letter_head, ["image"], as_dict=true) %}
{% set links = frappe.get_all('Dynamic Link', filters={'link_doctype': 'Company', 'link_name': doc.company, 'parenttype': 'Address'}, fields=['parent']) %}
{% if links %}
{% set address = frappe.db.get_value("Address", links[0].parent, ["*"], as_dict=true) %}
{% set patient = frappe.db.get_value("Patient", doc.patient, ["*"], as_dict=true) %}
{% set inpatient = frappe.db.get_value("Inpatient Record", doc.inpatient_record, ["*"], as_dict=true) %}
{% set dob = frappe.utils.getdate(patient.dob) %} {# Replace with actual DOB #}
{% set today = frappe.utils.nowdate() %}
{% set birth_date = frappe.utils.getdate(dob) %}
{% set current_date = frappe.utils.getdate(today) %}
{% set age = current_date.year - birth_date.year - ((current_date.month, current_date.day) < (birth_date.month, birth_date.day)) %}
{% set admission = frappe.utils.getdate(inpatient.admitted_datetime) %}
{% set discharge = frappe.utils.getdate(inpatient.discharge_datetime) %}
{% endif %}
<div class="invoice-container">
    <div class="header">
        <div class="">
            <img src="{{letter_head.image}}"  style="width:100px; margin-bottom:32px;" />

        </div>

        <div style="text-align: right;">
            {% if address.address_title %}
                <p style="font-weight: bold; font-size: large;">{{address.address_title}}<p/>
            {% endif %}
            {% if address.address_line1 %}
                <p>{{address.address_line1}}</p>
            {% endif %}
            {% if address.city %}
                <p>{{address.city}}</p>
            {% endif %}
            {% if address.pincode %}
                <p>{{address.pincode}}</p>
            {% endif %}
            <p>Tel: 4908128</p>
            <p>BRN: C22188669</p>
        </div>

    </div>

  <section class="patient-info">
    <h3>Patient Information</h3>
    <p><strong>Name:</strong> {{ doc.patient_name }}</p>
    <p><strong>Patient ID:</strong> {{ doc.patient }}</p>
    <p><strong>Tel:</strong> {{ patient.mobile }}</p>
    <p><strong>D.O.B:</strong> {{ frappe.utils.formatdate(patient.dob, "dd/MM/yyyy") }}  ({{age}}) years old</p>
  </section>

  <section class="invoice-details">
    <h3>Invoice Details</h3>
    <p><strong>Invoice No:</strong> {{ doc.name }}</p>
    <p><strong>Date:</strong> {{ frappe.utils.formatdate(doc.posting_date, "dd/MM/yyyy") }}</p>
    <p><strong>Date of Admission:</strong> {{ frappe.utils.formatdate(admission, "dd/MM/yyyy") }}</p>
    <p><strong>Date of Discharge:</strong> {{ frappe.utils.formatdate(discharge, "dd/MM/yyyy") }}</p>
  </section>

  <table class="invoice-table">
    <thead>
      <tr>
        <th>Item</th>
        <th>Qty</th>
        <th>Rate</th>
        <th>Amount</th>
      </tr>
    </thead>
    <tbody>
      {% for item in doc.items %}
      <tr>
        <td>{{ item.item_name }}</td>
        <td>{{ item.qty }}</td>
        <td>{{ frappe.utils.fmt_money(item.rate, currency=doc.currency) }}</td>
        <td>{{ frappe.utils.fmt_money(item.amount, currency=doc.currency) }}</td>
      </tr>
      {% endfor %}
    </tbody>
  </table>

  <footer>
    <div class="total-section">
      <p><strong>Net Total:</strong> {{ frappe.utils.fmt_money(doc.net_total, currency=doc.currency) }}</p>
      {% if doc.discount_amount > 0 %}
        <p><strong>Discount:</strong> {{ frappe.utils.fmt_money(doc.discount_amount, currency=doc.currency) }}</p>
        {% endif %}
      <p><strong>Total:</strong> {{ frappe.utils.fmt_money(doc.grand_total, currency=doc.currency) }}</p>
    </div>
    <div class="note-section">
      <p>Thank you for choosing our clinic. Wishing you a speedy recovery!</p>
    </div>
  </footer>
</div>
